import { useState, useEffect, createContext } from "react";
import "./DashBoard.css";
import SearchBar from "./SearchBar";
import EmployeeList from "./EmployeeList";

export const searchContext = createContext({
  // this ovrwrites state with the movie object if i use it in MovieItem
  employees: [],
  //addToMyList: (movie) => {
  //this.myList.push(movie)
  //   //setState({myList: this.state.myList})
});

const DashBoard = () => {
  const [employees, setEmployees] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    console.log("mounted");
    const url = "https://randomuser.me/api/?results=100";
    fetch(url)
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          setEmployees(result.results);
        },
        (error) => {
          setError(error);
        }
      );
  }, []);

  return (
    <searchContext.Provider value={{ employees, setEmployees }}>
      <div className="dashboard">
        <SearchBar />
        <EmployeeList employees={employees} error={error} />
      </div>
    </searchContext.Provider>
  );
};

export default DashBoard;
