import { useState } from "react";
import { Form, Button, Dropdown } from "react-bootstrap";
import "./AddEmployee.css";

const url = "http://localhost:3100/employee";

const AddEmployee = () => {
  const [form, setForm] = useState({});

  const setFormField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    alert("Employee submitted!");
  };

  return (
    <div className="add-employee">
      <Form>
        <Form.Group className="mb-3" controlId="FirstName">
          <Form.Label>First Name</Form.Label>
          <Form.Control
            type="text"
            onChange={(e) => setFormField("first_name", e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="LastName">
          <Form.Label>Last Name</Form.Label>
          <Form.Control
            type="text"
            onChange={(e) => setFormField("last_name", e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="Name">
          <Form.Label>Phone</Form.Label>
          <Form.Control
            type="text"
            onChange={(e) => setFormField("phone", e.target.value)}
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="Name">
          <Form.Label>Department</Form.Label>
          <Form.Control
            type="text"
            onChange={(e) => setFormField("department", e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="Name">
          <Form.Label>Picture URL</Form.Label>
          <Form.Control
            type="text"
            onChange={(e) => setFormField("picture", e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    </div>
  );
};

export default AddEmployee;
