  const imgURL = "https://image.tmdb.org/t/p/w300/";
  const imgPath = imgURL + props.employee.poster_path;
  return (
    <div className="employee-card">
      <img src={imgPath} />
      {props.employee.title}
      <button
        onClick={() => {
          console.log("add", setMyList);
          setMyList((prevState) => [...prevState, props.employee]);
        }}
      >
        {" "}
        +{" "}
      </button>
    </div>
  );
};

export default EmployeeCard;
